CREATE OR REPLACE PROCEDURE pls_desfazer_conf_envio_notif ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pagador_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_notificacao_pagador
	where	nr_seq_lote	= nr_seq_lote_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_notificacao_pagador
	set	ie_status	= ie_status_ant,
		ie_status_ant	 = NULL,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_pagador_w;

	end;
end loop;
close C01;

update	pls_notificacao_lote
set	dt_envio 	 = NULL,
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
