CREATE OR REPLACE PROCEDURE pls_desfazer_contas_medicas ( nr_seq_protocolo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_conta_pag_w			bigint;

BEGIN
select	count(*) 
into STRICT	qt_conta_pag_w 
from	pls_protocolo_conta 
where	nr_sequencia	= nr_seq_protocolo_p 
and	ie_status	= 3;
 
if (qt_conta_pag_w	> 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 266997, null); /* Protocolo liberado para pagamento, o protocolo n√£o sera excluido! */
end if;
CALL pls_excluir_protocolo_imp( nr_seq_protocolo_p, nm_usuario_p, cd_estabelecimento_p);
 
Update	ptu_fatura 
set	nr_seq_protocolo	 = NULL 
where	nr_seq_protocolo	= nr_seq_protocolo_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
