CREATE OR REPLACE PROCEDURE pls_desfazer_envio_fat_geral ( nr_seq_fat_geral_p ptu_fatura_geral.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

nr_seq_titulo_lote_w	pls_titulo_lote_camara.nr_sequencia%type;

BEGIN

select	max(a.nr_sequencia)
into STRICT	nr_seq_titulo_lote_w
from	pls_titulo_lote_camara	a,
	ptu_fatura_geral	b
where	a.nr_titulo_receber	= b.nr_titulo_receber
and	b.nr_sequencia		= nr_seq_fat_geral_p;

CALL pls_desvinc_tit_lote_camara(nr_seq_titulo_lote_w, nm_usuario_p, 'S');

update	ptu_fatura_geral
set	dt_postagem		 = NULL,
	nm_usuario_postagem	 = NULL,
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_fat_geral_p;

commit;

end;
$body$
