SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_fechar_pos_estab ( nr_seq_analise_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_grupo_liberado_w		integer;
qt_vinculado_lote_fat		integer;
ie_status_w			pls_analise_conta.ie_status%type;
nr_seq_lote_fat_w		pls_conta_pos_estabelecido.nr_seq_lote_fat%type;

BEGIN

select 	count(1)
into STRICT	qt_vinculado_lote_fat

where	exists (	SELECT	1
			from	pls_conta_pos_estabelecido a,
				pls_lote_faturamento b
			where	a.nr_seq_lote_fat = b.nr_sequencia
			and	a.nr_seq_analise = nr_seq_analise_p
			and	(a.nr_seq_lote_fat IS NOT NULL AND a.nr_seq_lote_fat::text <> '')
			and	((a.ie_situacao	= 'A') or (coalesce(a.ie_situacao::text, '') = ''))
			and	b.ie_tipo_lote <> 'A'
		);

select	max(nr_seq_lote_fat)
into STRICT	nr_seq_lote_fat_w
from	pls_conta_pos_estabelecido a,
	pls_lote_faturamento b
where	a.nr_seq_lote_fat = b.nr_sequencia
and	a.nr_seq_analise = nr_seq_analise_p
and	(a.nr_seq_lote_fat IS NOT NULL AND a.nr_seq_lote_fat::text <> '')
and	((a.ie_situacao	= 'A') or (coalesce(a.ie_situacao::text, '') = ''))
and	b.ie_tipo_lote <> 'A';

if (qt_vinculado_lote_fat > 0) then
	-- Nao e possivel desfazer fechamento da conta com itens vinculados a um lote de faturamento.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(289086, 'NR_SEQ_LOTE=' || nr_seq_lote_fat_w);
end if;

select 	count(1)
into STRICT	qt_grupo_liberado_w
 
where 	exists (	SELECT 	1 
			from 	pls_auditoria_conta_grupo
			where 	1=1
			and 	nr_seq_analise = nr_seq_analise_p
			and 	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		);

if (qt_grupo_liberado_w = 0 ) then -- Se nao data de liberacao muda para Aguardando analise
	ie_status_w := 'G';
else
	ie_status_w := 'L'; 	-- Se houver pelo menos um grupo com data de liberacao
				-- seta para Liberado para fechamento
end if;

update	pls_analise_conta
set	ie_status = ie_status_w
where 	nr_sequencia	= nr_seq_analise_p;
	
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
