CREATE OR REPLACE PROCEDURE pls_desfazer_franq_pag ( nr_seq_franq_pag_p pls_franq_pag_prest.nr_seq_franq_pag%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


vl_lote_w	pls_lote_pagamento.vl_lote%type;
nr_seq_lote_w	pls_lote_pagamento.nr_sequencia%type;

c01 CURSOR(nr_seq_franq_pag_w	pls_franq_pag_prest.nr_seq_franq_pag%type) FOR
SELECT	nr_sequencia
from	pls_franq_pag_prest
where	nr_seq_franq_pag	= nr_seq_franq_pag_w;

BEGIN

for	r_c01_w in c01(nr_seq_franq_pag_p) loop

	delete 	from pls_franq_pag_prest_prod a
	where	a.nr_seq_franq_pag_prest	= r_c01_w.nr_sequencia;

	CALL pls_atualizar_franq_pag_prest(r_c01_w.nr_sequencia, nm_usuario_p, null);

end loop;

update 	pls_franq_pag
set	dt_geracao_lote  = NULL
where	nr_sequencia = nr_seq_franq_pag_p;
--alteração aldellandrea
select	nr_seq_lote_pgto
into STRICT	nr_seq_lote_w
from	pls_franq_pag
where	nr_sequencia = nr_seq_franq_pag_p;

select	coalesce(sum(vl_pagamento),0)
into STRICT	vl_lote_w
from	pls_pagamento_prestador
where	nr_seq_lote	= nr_seq_lote_w;

update	pls_lote_pagamento
set	vl_lote		= vl_lote_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_lote_w;
--fim alteração
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
