SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_imp_arq_dev ( nr_seq_devolucao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w	integer;

C01 CURSOR(	nr_seq_devolucao_pc	pls_sib_devolucao.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	pls_sib_devolucao_erro
	where	nr_seq_devolucao = nr_seq_devolucao_pc;

C02 CURSOR(	nr_seq_dev_erro_pc	pls_sib_devolucao_erro.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	pls_sib_dev_erro_campo
	where	nr_seq_dev_erro = nr_seq_dev_erro_pc;
BEGIN

if (nr_seq_devolucao_p > 0) then
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_sib_devolucao_cco
	where	nr_seq_devolucao	= nr_seq_devolucao_p
	and	(dt_confirmacao IS NOT NULL AND dt_confirmacao::text <> '');

	if (qt_registros_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(801966); --Não é possível excluir o arquivo pois já foi realizada a atualização do CCO para os beneficiários
	end if;

	delete	from pls_sib_devolucao_cco
	where	nr_seq_devolucao = nr_seq_devolucao_p;

	delete	from pls_sib_devolucao_resumo
	where	nr_seq_devolucao = nr_seq_devolucao_p;

	delete	from pls_sib_devolucao_msg
	where	nr_seq_devolucao = nr_seq_devolucao_p;


	for r_C01_w in C01(nr_seq_devolucao_p) loop
		begin

		for r_c02_w in C02(r_C01_w.nr_sequencia) loop
			begin
			delete	from	pls_sib_dev_erro_conteudo
			where	nr_seq_dev_erro_campo	= r_c02_w.nr_sequencia;

			delete	from pls_sib_dev_erro_campo
			where	nr_sequencia = r_c02_w.nr_sequencia;
			end;
		end loop;

		delete	from pls_sib_devolucao_erro
		where	nr_sequencia = r_C01_w.nr_sequencia;
		end;
	end loop;

	delete	from pls_sib_devolucao
	where	nr_sequencia = nr_seq_devolucao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
