CREATE OR REPLACE PROCEDURE pls_desfazer_liberacao_lib_fat (nr_seq_lib_fat_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_fat_w	pls_lote_fat_lib.nr_seq_lib_fat%type;


BEGIN

select	max(nr_seq_lote_fat)
into STRICT	nr_seq_lote_fat_w
from	pls_lote_fat_lib
where	nr_seq_lib_fat	= nr_seq_lib_fat_p;

if (nr_seq_lote_fat_w IS NOT NULL AND nr_seq_lote_fat_w::text <> '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(281758, 'NR_SEQ_LOTE_FAT_P=' || nr_seq_lote_fat_w);
end if;

update	pls_lib_faturamento
set	dt_liberacao	 = NULL,
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_lib_fat_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
