CREATE OR REPLACE PROCEDURE pls_desfazer_liberacao_perecia ( nr_seq_pericia_p bigint, nm_usuario_p text) AS $body$
BEGIN

update	pls_pericia_medica
set	ie_status			= 'U',
	dt_liberacao			 = NULL,
	dt_liberacao_atendimento	 = NULL,
	dt_atualizacao			= clock_timestamp(),
	nm_usuario			= nm_usuario_p
where	nr_sequencia			= nr_seq_pericia_p;

insert into pls_pericia_log(	nr_sequencia, nr_seq_pericia, ds_log,
		dt_atualizacao, nm_usuario)
values (	nextval('pls_pericia_log_seq'), nr_seq_pericia_p, 'Desfeita a liberação da perícia médica' || chr(13) ||'Data e hora: '|| clock_timestamp(),
		clock_timestamp(),nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
