CREATE OR REPLACE PROCEDURE pls_desfazer_liberar_quest ( nm_usuario_p pls_quest_processo_judic.nm_usuario%type, nr_seq_quest_p pls_quest_processo_judic.nr_sequencia%type, ie_acao_p text) AS $body$
BEGIN
-- This procedure is responsible to perform the release and undo action of a questionnaire

-- When ie_acao_p = 'L' the action will be 'release' filling dt_liberacao field.

-- When ie_acao_p = 'D' the action will be 'undo' erasing the content on dt_liberacao field.
if (ie_acao_p = 'L') then
	begin
	update	pls_quest_processo_judic
	set		dt_liberacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_quest_p;
	end;
elsif (ie_acao_p = 'D') then
	begin
	update	pls_quest_processo_judic
	set		dt_liberacao  = NULL,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_quest_p;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
