CREATE OR REPLACE PROCEDURE pls_desfazer_lib_analise_conta ( nr_seq_resumo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_conta_w			bigint;
nr_seq_item_w			bigint;
ie_tipo_item_w			varchar(1);

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		'P' 
	from	pls_conta_proc 
	where	nr_seq_conta	= nr_seq_conta_w 
	and	ie_status	= 'L' 
	
union
 
	SELECT	nr_sequencia, 
		'M' 
	from	pls_conta_mat 
	where	nr_seq_conta	= nr_seq_conta_w 
	and	ie_status	= 'L';


BEGIN 
 
select 	nr_seq_conta 
into STRICT	nr_seq_conta_w 
from	w_pls_resumo_conta 
where	nr_sequencia	= nr_seq_resumo_p;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_item_w, 
	ie_tipo_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL pls_desfazer_lib_item(nr_seq_item_w, ie_tipo_item_w, cd_estabelecimento_p, nm_usuario_p);
	end;
end loop;
close C01;
 
update	w_pls_resumo_conta 
set	ie_situacao	 = NULL, 
	ie_status	 = NULL, 
	dt_atualizacao	= clock_timestamp(), 
	nm_usuario	= nm_usuario_p 
where	nr_sequencia	= nr_seq_resumo_p;
	 
commit;
 
end;
