SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_lib_declaracao ( nr_seq_declaracao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_benef_analise_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_benef_analise_w
from	pls_declaracao_segurado a,
	pls_proposta_adesao b,
	pls_proposta_beneficiario c,
	pls_analise_adesao d
where	b.nr_sequencia		= a.nr_seq_proposta_adesao
and	b.nr_sequencia		= c.nr_seq_proposta
and	c.nr_sequencia		= d.nr_seq_pessoa_proposta
and	a.nr_sequencia		= nr_seq_declaracao_p
and	coalesce(ie_forma_preenchimento,'O')	= 'B'
and	d.ie_status <> 'E';

if (qt_benef_analise_w = 0) then
	update	pls_declaracao_segurado
	set	dt_liberacao	 = NULL,
		ie_status	= 'U',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_declaracao_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 240203, null );
	/* A declaração está em análise. Não é possível desfazer a liberação. */

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
