CREATE OR REPLACE PROCEDURE pls_desfazer_lib_glosas ( ds_seq_itens_analise_p text, nr_seq_grupo_atual_p bigint, nr_seq_analise_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE

 
ds_seq_itens_analise_w	varchar(4000);
nr_seq_item_analise_w	varchar(10);
nr_seq_conta_w		bigint;
nr_seq_proc_w		bigint;
nr_seq_mat_w		bigint;
nr_seq_glosa_oc_w	bigint;
ie_tipo_w		varchar(2);
nr_sequencia_w		bigint;

C01 CURSOR FOR 
	SELECT	ie_tipo, 
		nr_seq_glosa_oc,		 
		nr_sequencia 
	from	pls_analise_conta_item 
	where	((nr_seq_conta = nr_seq_conta_w and ((coalesce(nr_seq_conta_proc,0) = 0) and (coalesce(nr_seq_conta_mat,0) = 0))) 
	or	 (nr_seq_conta = nr_seq_conta_w and ((nr_seq_conta_proc = nr_seq_proc_w) or (nr_seq_conta_mat = nr_seq_mat_w)))) 
	and	ie_tipo = 'O' 
	and	pls_obter_dados_auditor(cd_codigo, null, nm_usuario_p, 'C', nr_seq_grupo_atual_p) = 'S' 
	and	ie_status in ('A', 'L', 'N');
	

BEGIN 
ds_seq_itens_analise_w := ds_seq_itens_analise_p;
 
while(position(',' in ds_seq_itens_analise_w) <> 0) loop 
	begin			 
	 
	--obtem-se a primeira regra 
	nr_seq_item_analise_w	:= substr(ds_seq_itens_analise_w,1,position(',' in ds_seq_itens_analise_w)-1);
	--remove-se essa do conjunto	 
	ds_seq_itens_analise_w	:= substr(ds_seq_itens_analise_w,position(',' in ds_seq_itens_analise_w)+1,255);
	 
	select	nr_seq_conta, 
		CASE WHEN ie_tipo_item='P' THEN  nr_seq_item WHEN ie_tipo_item='M' THEN  null END , 
		CASE WHEN ie_tipo_item='P' THEN  null WHEN ie_tipo_item='M' THEN  nr_seq_item END  
	into STRICT	nr_seq_conta_w, 
		nr_seq_proc_w, 
		nr_seq_mat_w 
	from	w_pls_resumo_conta 
	where	nr_sequencia = nr_seq_item_analise_w;
	  
	open C01;
	loop 
	fetch C01 into	 
		ie_tipo_w, 
		nr_seq_glosa_oc_w,		 
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		begin 
		CALL pls_desfazer_lib_glosa_oc(	nr_sequencia_w, nr_seq_glosa_oc_w, nm_usuario_p,	 
						nr_seq_conta_w, nr_seq_analise_p, nr_seq_glosa_oc_w, 	 
						ie_tipo_w, cd_estabelecimento_p, nr_seq_grupo_atual_p, 
						nr_seq_item_analise_w, 'N','N');
		exception 
		when others then 
			null;
		end;
		 
		end;
	end loop;
	close C01;
	 
	end;
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
