CREATE OR REPLACE PROCEDURE pls_desfazer_lib_guia_plano ( cd_guia_p text, nr_seq_guia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE


qt_count_w 	bigint;


BEGIN

if (cd_guia_p IS NOT NULL AND cd_guia_p::text <> '') and (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_count_w
	from	pls_conta a,
		pls_protocolo_conta b
	where	a.nr_seq_protocolo	= b.nr_sequencia
	and	a.cd_guia		= cd_guia_p;

	if (qt_count_w > 0) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(38179,'');
		--Esta guia já foi faturada, a liberação não pode ser desfeita!
		end;
	end if;

	CALL pls_desfazer_lib_guia(nr_seq_guia_p, cd_estabelecimento_p, nm_usuario_p);
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
