CREATE OR REPLACE PROCEDURE pls_desfazer_lib_guia ( nr_seq_guia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_glosa_w			varchar(1)	:= 'N';
ie_estagio_w			smallint;
ie_tipo_processo_w		varchar(2);
ie_tipo_intercambio_w		varchar(2);


BEGIN
ie_glosa_w	:= pls_obter_se_guia_glosada(nr_seq_guia_p);

if (ie_glosa_w	= 'S') then
	ie_estagio_w	:= 2;
else
	ie_estagio_w	:= 3;
end if;

begin
	select	ie_tipo_processo,
		ie_tipo_intercambio
	into STRICT	ie_tipo_processo_w,
		ie_tipo_intercambio_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;
exception
when others then
	ie_tipo_processo_w	:= null;
	ie_tipo_intercambio_w	:= null;
end;

if (ie_tipo_processo_w	= 'I') and (ie_tipo_intercambio_w	= 'E') then
	-- Você não pode realizar esta ação em uma guia recebida via intercâmbio!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(271396);
end if;

if (coalesce(nr_seq_guia_p,0) <> 0) then
	update	pls_guia_plano
	set	ie_status		= '2',
		dt_autorizacao		 = NULL,
		dt_validade_senha	 = NULL,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_liberacao		 = NULL,
		nm_usuario_liberacao	 = NULL,
		ie_cobranca_prevista	= 'N',
		ie_estagio		= ie_estagio_w
	where	nr_sequencia		= nr_seq_guia_p;

	CALL pls_guia_gravar_historico(nr_seq_guia_p, 6, ' Estágio: ' || ie_estagio_w ||
		' / Estabelecimento: ' || cd_estabelecimento_p, null, nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
