SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_lib_inco_proposta ( nr_seq_validacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proposta_w		bigint;
qt_inconsistencia_w		bigint;


BEGIN

select	nr_seq_proposta
into STRICT	nr_seq_proposta_w
from	pls_proposta_validacao
where	nr_sequencia	= nr_seq_validacao_p;

update	pls_proposta_validacao
set	dt_liberacao	 = NULL,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	ie_consistido	= 'I'
where	nr_sequencia	= nr_seq_validacao_p;

select	count(*)
into STRICT	qt_inconsistencia_w
from	pls_proposta_validacao
where	nr_seq_proposta	= nr_seq_proposta_w
and	ie_consistido	= 'I';

/*aaschlote OS - 303632 24/06/2011 */

if (qt_inconsistencia_w	> 0) then
	update	pls_proposta_adesao
	set	ie_inconsistencia	= 'S'
	where	nr_sequencia	= nr_seq_proposta_w;
else
	update	pls_proposta_adesao
	set	ie_inconsistencia	= 'N'
	where	nr_sequencia		= nr_seq_proposta_w;
end if;


delete	FROM pls_proposta_check_list
where	nr_seq_validacao = nr_seq_validacao_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
