CREATE OR REPLACE PROCEDURE pls_desfazer_lib_item_despesa ( nr_seq_conta_p bigint, ie_tipo_despesa_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_item_w			bigint;
ie_tipo_result_w		varchar(1);
ie_tipo_despesa_w		smallint;
nr_seq_analise_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		'M' 
	from	pls_conta_mat 
	where	nr_seq_conta	= nr_seq_conta_p 
	and	ie_tipo_despesa	= ie_tipo_despesa_w 
	and	coalesce(ie_status,'U') <> 'D' 
	and	ie_tipo_despesa_p in ('GM','ME','M','O') 
	
union
 
	SELECT	nr_sequencia, 
		'P' 
	from	pls_conta_proc 
	where	nr_seq_conta	= nr_seq_conta_p 
	and	ie_tipo_despesa	= ie_tipo_despesa_w 
	and	coalesce(ie_status,'U') <> 'D' 
	and	ie_tipo_despesa_p in ('P', 'T', 'D', 'PCT');


BEGIN 
if (ie_tipo_despesa_p in ('P','GM')) then 
	ie_tipo_despesa_w	:= 1;
elsif (ie_tipo_despesa_p in ('T','ME')) then 
	ie_tipo_despesa_w	:= 2;
elsif (ie_tipo_despesa_p in ('D','M')) then 
	ie_tipo_despesa_w	:= 3;
elsif (ie_tipo_despesa_p	= 'PCT') then 
	ie_tipo_despesa_w	:= 4;
elsif (ie_tipo_despesa_p	= 'O') then 
	ie_tipo_despesa_w	:= 7;
end if;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_item_w, 
	ie_tipo_result_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL pls_desfazer_lib_item(nr_seq_item_w, ie_tipo_result_w, cd_estabelecimento_p, nm_usuario_p);
	end;
end loop;
close C01;
 
select	nr_seq_analise 
into STRICT	nr_seq_analise_w 
from	pls_conta 
where	nr_sequencia = nr_seq_conta_p;
 
update	pls_analise_conta 
set	ie_status = 'S' 
where	nr_sequencia	= nr_seq_analise_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
