SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_lib_itens ( ds_lista_des_lib_P text, ie_tipo_item_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_des_lib_W	varchar(2000);
ie_status_w		varchar(1);
nr_pos_virgula_w	bigint;
nr_sequencia_w		bigint;


BEGIN 
if (ds_lista_des_lib_P IS NOT NULL AND ds_lista_des_lib_P::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	ds_lista_des_lib_W := ds_lista_des_lib_P;
	 
	while(ds_lista_des_lib_W IS NOT NULL AND ds_lista_des_lib_W::text <> '') loop 
		begin 
		 
		nr_pos_virgula_w	:= position(',' in ds_lista_des_lib_W);
		 
		if (nr_pos_virgula_w > 0) then 
			begin			 
			nr_sequencia_w		:= (substr(ds_lista_des_lib_W,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_des_lib_W	:= substr(ds_lista_des_lib_W,nr_pos_virgula_w+1,length(ds_lista_des_lib_W));			
			end;
		else 
			begin 
			nr_sequencia_w		:= (ds_lista_des_lib_W)::numeric;
			ds_lista_des_lib_W	:= null;
			end;
		end if;
			 
		if (nr_sequencia_w > 0) then 
			begin			 
			if (ie_tipo_item_p = 'P') then 
				begin 
				select	max(ie_status) 
				into STRICT	ie_status_w 
				from	pls_conta 
				where	nr_sequencia = nr_sequencia_w;
				end;
			elsif (ie_tipo_item_p = 'M') then 
				begin 
				select	max(ie_status) 
				into STRICT	ie_status_w 
				from	pls_conta_mat 
				where	nr_sequencia = nr_sequencia_w;
				end;
			end if;
			 
			if (ie_status_w = 'L') or (ie_status_w = 'S') then 
				begin 
				CALL pls_desfazer_lib_item( 
					nr_sequencia_w, 
					ie_tipo_item_p, 
					cd_estabelecimento_p, 
					nm_usuario_p);
				end;
			end if;
			end;
		end if;	
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
