SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_lib_processo ( nr_seq_processo_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_reg_w			integer;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	pls_req_liminar_judicial 	b,
	pls_requisicao			a
where	a.nr_sequencia		= b.nr_seq_requisicao
and	b.nr_seq_processo	= nr_seq_processo_p;

if (qt_reg_w = 0) then
	update	processo_judicial_liminar
	set	ie_estagio	= 1,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_liberacao	 = NULL,
		nm_usuario_lib	 = NULL
	where	nr_sequencia	= nr_seq_processo_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(113991,'');
	--Liminar já cadastrada para uma requisição.
	--Não é possível desfazer a liberação!
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
