SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_lib_prot_reemb ( nr_seq_protocolo_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w			bigint := 0;


BEGIN

select	count(1)
into STRICT	qt_registro_w
from	pls_protocolo_conta	b,
	titulo_pagar		a
where	b.nr_sequencia	= a.nr_seq_reembolso
and	((a.ie_situacao	= 'L') or (a.ie_situacao = 'A'))
and	b.nr_sequencia = nr_seq_protocolo_p;

if (qt_registro_w <> 0) then
	--'O reembolso possui títulos vinculados, é necessário primeiro cancelar os títulos pela função "Títulos a pagar".');
	CALL wheb_mensagem_pck.exibir_mensagem_abort(236364);
else
	qt_registro_w :=0;

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_protocolo_conta	a,
		lote_contabil		b
	where	b.nr_lote_contabil	= a.nr_lote_contabil
	and	a.nr_sequencia		= nr_seq_protocolo_p
	and	a.nr_lote_contabil	> 0;

	if (qt_registro_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(236365);
		--20011,'O reembolso já está vinculado a um lote contábil.');
	else
		update	pls_protocolo_conta
		set	ie_status	 = '5',
			dt_atualizacao	 = clock_timestamp(),
			dt_lib_pagamento  = NULL,
			nm_usuario	 = nm_usuario_p
		where	nr_sequencia 	 = nr_seq_protocolo_p;

		update	pls_conta_medica_resumo
		set	ie_status_protocolo 	= '5',
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_protocolo	= nr_seq_protocolo_p
		and	ie_situacao = 'A';
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_desfazer_lib_prot_reemb ( nr_seq_protocolo_p bigint, nm_usuario_p text) FROM PUBLIC;

