SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_lib_repasse ( nr_seq_item_repasse_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_repasse_w	bigint;
dt_aprovacao_w		timestamp;
nr_lote_contabil_w	lote_contabil.nr_lote_contabil%type;


BEGIN

select	nr_seq_repasse
into STRICT	nr_seq_repasse_w
from	pls_repasse_mens
where	nr_sequencia = nr_seq_item_repasse_p;

select	max(dt_aprovacao)
into STRICT	dt_aprovacao_w
from	pls_repasse_vend
where	nr_sequencia	= nr_seq_repasse_w;

select	coalesce(max(nr_lote_contabil),0)
into STRICT	nr_lote_contabil_w
from (SELECT	v.nr_lote_contabil
	from	pls_repasse_vend v
	where	v.nr_sequencia	= nr_seq_repasse_w
	
union

	SELECT	l.nr_lote_contabil
	from	pls_repasse_vend	v,
		pls_repasse_lanc	l
	where	v.nr_sequencia	= l.nr_seq_repasse
	and	v.nr_sequencia	= nr_seq_repasse_w) alias2;

if (nr_lote_contabil_w <> 0) then
	/* Não é possível concluir a ação executada pois o lote contábil #@NR_LOTE_CONTABIL#@ está vinculado ao repasse!*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(458979, 'NR_LOTE_CONTABIL=' || nr_lote_contabil_w);
end if;

update	pls_repasse_mens
set	nr_seq_repasse  = NULL,
	nm_usuario_lib  = NULL,
	dt_liberacao  = NULL
where	nr_sequencia = nr_seq_item_repasse_p;

if (coalesce(dt_aprovacao_w::text, '') = '') then
	delete	from	pls_repasse_vend_venc
	where	nr_seq_repasse	= nr_seq_repasse_w;

	CALL pls_gerar_venc_repasse(nr_seq_repasse_w, 'S', nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
