CREATE OR REPLACE PROCEDURE pls_desfazer_lote_apres ( nr_seq_lote_p bigint, nr_seq_guia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

delete	FROM pls_apres_automatica_guia
where	nr_seq_lote	= nr_seq_lote_p
and	((nr_seq_guia = nr_seq_guia_p) or (coalesce(nr_seq_guia_p::text, '') = ''));

update	pls_lote_apres_automatica
set	ie_status = '2'
where	nr_sequencia = nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
