SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_lote_comissao ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
/* Campos do cursor 1 */
			 
nr_seq_comissao_w		bigint;
nr_seq_canal_venda_w		bigint;
dt_referencia_w			timestamp;

/* Campos do cursor 2 */
 
nr_seq_benef_comissao_w		bigint;
dt_estorno_w			timestamp;
vl_estorno_w			double precision;

nr_seq_estorno_w		bigint;	
nr_seq_estorno_comis_w		pls_estorno_comissao.nr_sequencia%type;

C01 CURSOR FOR 
	SELECT	b.nr_sequencia, 
		b.nr_seq_canal_venda, 
		a.dt_referencia 
	from	pls_comissao		b, 
		pls_lote_comissao	a 
	where	a.nr_sequencia	= b.nr_seq_lote 
	and	a.nr_sequencia 	= nr_seq_lote_p;

C02 CURSOR FOR 
	SELECT	nr_sequencia, 
		dt_estorno, 
		vl_estorno 
	from	pls_comissao_beneficiario 
	where	nr_seq_comissao = nr_seq_comissao_w;
	
C03 CURSOR FOR 
	SELECT	c.nr_sequencia 
	from	pls_estorno_comissao		c, 
		pls_comissao_beneficiario	b, 
		pls_comissao			a 
	where	a.nr_sequencia = b.nr_seq_comissao 
	and	a.nr_seq_canal_venda = c.nr_seq_canal_venda 
	and	b.nr_seq_segurado = c.nr_seq_segurado 
	and	b.nr_sequencia = nr_seq_benef_comissao_w;	
 

BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_seq_comissao_w, 
	nr_seq_canal_venda_w, 
	dt_referencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	open C02;
	loop 
	fetch C02 into 
		nr_seq_benef_comissao_w, 
		dt_estorno_w, 
		vl_estorno_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		nr_seq_estorno_w := null;
	 
		delete	from	pls_comissao_benef_item 
		where	nr_seq_comissao_benef = nr_seq_benef_comissao_w;
		 
		if (dt_estorno_w IS NOT NULL AND dt_estorno_w::text <> '') then /* Se futuramente este beneficiário for estornado... */
 
			open C03;
			loop 
			fetch C03 into	 
				nr_seq_estorno_comis_w;
			EXIT WHEN NOT FOUND; /* apply on C03 */
				begin 
				 
				delete from pls_estorno_comissao 
				where	nr_sequencia = nr_seq_estorno_comis_w;
				 
				end;
			end loop;
			close C03;	
		elsif (vl_estorno_w < 0) then /* Se na geração deste lote for a aplicação do estorno... */
 
			begin 
			select	nr_sequencia 
			into STRICT	nr_seq_estorno_w 
			from	pls_estorno_comissao	 
			where	nr_seq_comissao_benef = nr_seq_benef_comissao_w;
			exception 
			when others then 
				nr_seq_estorno_w := null;
			end;
		 
			update	pls_estorno_comissao 
			set	nr_seq_comissao_benef  = NULL 
			where	nr_sequencia = nr_seq_estorno_w;	
		end if;
 
		update	pls_repasse_mens 
		set	nr_seq_comissao_benef  = NULL 
		where	nr_seq_comissao_benef = nr_seq_benef_comissao_w;
	 
	end loop;
	close C02;
	 
	delete	from	pls_comissao_beneficiario 
	where	nr_seq_comissao = nr_seq_comissao_w;
	 
	delete	from	pls_comissao_titulo 
	where	nr_seq_comissao = nr_seq_comissao_w;
	 
	CALL pls_gerar_desf_comis_lanc_adic(nr_seq_canal_venda_w, nr_seq_comissao_w, dt_referencia_w, nm_usuario_p, cd_estabelecimento_p, 'D');
	 
end loop;
close C01;
 
delete	from	pls_comissao 
where	nr_seq_lote = nr_seq_lote_p;
 
update	pls_lote_comissao 
set	dt_geracao 		 = NULL, 
	dt_geracao_titulo 	 = NULL, 
	vl_comissao		 = NULL, 
	vl_titulo		 = NULL, 
	vl_folha_pag		 = NULL, 
	ie_status		= 'U' -- Usuário (aguardando ação) 
where 	nr_sequencia = nr_seq_lote_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_desfazer_lote_comissao ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

