CREATE OR REPLACE PROCEDURE pls_desfazer_lote_guia_mens ( nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_guia_mens_arq_w		bigint;
nr_seq_mens_guia_envio_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_mens_guia_arquivo
	where	nr_seq_lote	= nr_seq_lote_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_mensalidade_guia_envio
	where	nr_seq_guia_arquivo = nr_seq_guia_mens_arq_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_guia_mens_arq_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_mens_guia_envio_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		delete	FROM pls_mens_guia_envio_proc
		where	NR_SEQ_GUIA_ENVIO	= nr_seq_mens_guia_envio_w;

		delete	FROM pls_mens_guia_envio_mat
		where	NR_SEQ_GUIA_ENVIO	= nr_seq_mens_guia_envio_w;

		update	pls_mensalidade_seg_item
		set	nr_seq_item_envio_guia	 = NULL
		where	nr_seq_item_envio_guia	= nr_seq_mens_guia_envio_w;

		delete	FROM pls_mensalidade_guia_envio
		where	nr_sequencia	= nr_seq_mens_guia_envio_w;

		end;
	end loop;
	close C02;

	delete	FROM pls_mens_guia_arquivo
	where	nr_sequencia	= nr_seq_guia_mens_arq_w;

	end;
end loop;
close C01;

update	pls_lote_mens_guia_envio
set	dt_geracao_lote	 = NULL
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
$body$
