CREATE OR REPLACE PROCEDURE pls_desfazer_lote_import ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lanc_prog_imp_w		pls_lanc_prog_importacao.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_lanc_prog_importacao
	where	nr_seq_lote = nr_seq_lote_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_lanc_prog_imp_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM pls_lanc_prog_imp_inco
	where	nr_seq_lanc_prog_imp	= nr_seq_lanc_prog_imp_w;

	delete	FROM pls_lanc_prog_importacao
	where	nr_sequencia		= nr_seq_lanc_prog_imp_w;

	end;
end loop;
close C01;

update	pls_lanc_prog_import_lote
set	nm_arquivo		 = NULL,
	nr_seq_tipo_imp_lanc	 = NULL
where	nr_sequencia		= nr_seq_lote_p;

commit;

end;
$body$
