SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_lote_recuperacao ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_seq_lote_w		pls_lote_reemb_recuperacao.nr_sequencia%type;

BEGIN
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	select 	max(a.nr_sequencia)
	into STRICT	nr_seq_lote_w
	from	pls_lote_reemb_recuperacao a
	where	a.nr_sequencia	= nr_seq_lote_p
	and	(a.dt_geracao_lote IS NOT NULL AND a.dt_geracao_lote::text <> '')
	and	coalesce(a.dt_geracao_titulo::text, '') = '';

	if (nr_seq_lote_w IS NOT NULL AND nr_seq_lote_w::text <> '') then
		delete
		from	pls_lote_reemb_rec_item
		where	nr_seq_lote = nr_seq_lote_w;
		update	pls_lote_reemb_recuperacao
		set	dt_geracao_lote	 = NULL,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp(),
			vl_lote		= 0
		where	nr_sequencia	= nr_seq_lote_w;

		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
