SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_lote_solic_alt_pf ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Desfazer os dados gerados no lote de alteração de PF
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registros_w		bigint;


BEGIN
select	count(1)
into STRICT	qt_registros_w
from	pls_revisao_solic_alt_pf	b,
	tasy_solic_alteracao		a
where	b.nr_seq_solic_alt		= a.nr_sequencia
and	b.nr_seq_lote			= nr_seq_lote_p
and	a.ie_status			<> 'A'  LIMIT 1;

if (qt_registros_w	> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(222900);
end if;

delete	FROM pls_revisao_solic_alt_pf
where	nr_seq_lote			= nr_seq_lote_p;

update	pls_lote_pessoa_inconsist
set	dt_geracao_lote	 = NULL
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
