CREATE OR REPLACE PROCEDURE pls_desfazer_lote_ted_copartic ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ted_pagador_w	pls_ted_copartic_pagador.nr_sequencia%type;
nr_seq_ted_benef_w	pls_ted_copartic_benef.nr_sequencia%type;
nr_seq_ted_item_w	pls_ted_copartic_item.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_ted_copartic_pagador
	where	nr_seq_lote	= nr_seq_lote_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_ted_copartic_benef
	where	nr_seq_ted_pagador	= nr_seq_ted_pagador_w;

C03 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_ted_copartic_item
	where	nr_seq_ted_benef	= nr_seq_ted_benef_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_ted_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_ted_benef_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		open C03;
		loop
		fetch C03 into
			nr_seq_ted_item_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin
			delete	from	pls_ted_copartic_item
			where	nr_sequencia	= nr_seq_ted_item_w;
			end;
		end loop;
		close C03;

		delete	from	pls_ted_copartic_benef
		where	nr_sequencia	= nr_seq_ted_benef_w;
		end;
	end loop;
	close C02;

	delete	from	pls_ted_copartic_pagador
	where	nr_sequencia	= nr_seq_ted_pagador_w;
	end;
end loop;
close C01;

update	pls_lote_ted_copartic
set	ie_status	= '1',
	dt_geracao_lote	 = NULL
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
$body$
