CREATE OR REPLACE PROCEDURE pls_desfazer_movto_ret_trib ( nr_seq_lote_ret_p pls_lote_retencao_trib.nr_sequencia%type, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

					
nr_seq_lote_evento_w		pls_lote_evento.nr_sequencia%type;
ie_concil_contab_w		pls_visible_false.ie_concil_contab%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_lote_evento_w
from	pls_lote_evento
where	nr_seq_lote_ret_trib = nr_seq_lote_ret_p;

select	coalesce(max(ie_concil_contab), 'N')
into STRICT 	ie_concil_contab_w
from	pls_visible_false
where 	cd_estabelecimento = cd_estabelecimento_p;

if (ie_concil_contab_w = 'S') then
	CALL pls_ctb_onl_gravar_movto_pck.gravar_movto_desf_ret_trib(nr_seq_lote_evento_w, cd_estabelecimento_p, nm_usuario_p);
end if;

if (nr_seq_lote_evento_w IS NOT NULL AND nr_seq_lote_evento_w::text <> '') then
	delete 	from pls_evento_movimento
	where	nr_seq_lote = nr_seq_lote_evento_w;
	
	delete 	from pls_lote_evento
	where	nr_sequencia = nr_seq_lote_evento_w;
end if;

--commit;
end;
$body$
