CREATE OR REPLACE PROCEDURE pls_desfazer_mov_produto_a300 ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_empresa_w	bigint;
nr_seq_benef_w		bigint;
nr_seq_benef_compl_w	bigint;
nr_seq_lote_w		bigint;
nr_seq_mov_plano_w	bigint;
qt_w_ptu_uniodonto_w	bigint;


C00 CURSOR FOR
	SELECT	nr_sequencia
	from	ptu_movimentacao_produto
	where	nr_seq_lote	= nr_seq_lote_p;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	ptu_mov_produto_empresa
	where	nr_seq_mov_produto	= nr_seq_mov_plano_w;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	ptu_mov_produto_benef
	where	nr_seq_empresa	= nr_seq_empresa_w;

C03 CURSOR FOR
	SELECT	nr_sequencia
	from	ptu_movimento_benef_compl
	where	nr_seq_beneficiario	= nr_seq_benef_w;


BEGIN

select	count(*)
into STRICT	qt_w_ptu_uniodonto_w
from	w_ptu_movto_produto
where	nr_seq_lote_movto	= nr_seq_lote_p;

if (qt_w_ptu_uniodonto_w > 0) then
	delete	FROM w_ptu_movto_produto
	where	nr_seq_lote_movto	= nr_seq_lote_p;
end if;

open C00;
loop
fetch C00 into
	nr_seq_mov_plano_w;
EXIT WHEN NOT FOUND; /* apply on C00 */
	begin
	open C01;
	loop
	fetch C01 into
		nr_seq_empresa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		open C02;
		loop
		fetch C02 into
			nr_seq_benef_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			open C03;
			loop
			fetch C03 into
				nr_seq_benef_compl_w;
			EXIT WHEN NOT FOUND; /* apply on C03 */
				begin

				delete	FROM ptu_movimento_benef_compl
				where	nr_sequencia	=  nr_seq_benef_compl_w;

				end;
			end loop;
			close C03;

			delete 	FROM ptu_intercambio_consist
			where	nr_seq_mov_prod_benef = nr_seq_benef_w;

			delete	FROM ptu_mov_produto_benef
			where	nr_sequencia	=  nr_seq_benef_w;

			end;
		end loop;
		close C02;

		delete	FROM ptu_mov_produto_empresa
		where	nr_sequencia	=  nr_seq_empresa_w;

		end;
	end loop;
	close C01;

	delete	FROM ptu_movimentacao_produto
	where	nr_sequencia	= nr_seq_mov_plano_w;

	end;
end loop;
close C00;

update	ptu_mov_produto_lote
set	dt_geracao_arquivo  = NULL
where	nr_sequencia	= nr_seq_lote_p;

delete	FROM ptu_mov_produto_log
where	nr_seq_lote	= nr_seq_lote_p;

delete	FROM w_ptu_movimentacao_resumo
where	nr_seq_lote	= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
