CREATE OR REPLACE PROCEDURE pls_desfazer_notificacao ( nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Desfaz o lote de notificações
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
	OPS - Controle de notificações de atraso
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_pagador_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_notificacao_pagador
	where	nr_seq_lote	= nr_seq_lote_p;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	cobranca a
	set	a.nr_seq_notific_item	 = NULL
	where	a.ie_status = 'C'
	and	exists (	SELECT	1
			from	pls_notificacao_item x
			where	x.nr_sequencia	= a.nr_seq_notific_item
			and	x.nr_seq_notific_pagador = nr_seq_pagador_w);

	delete	from	pls_notificacao_item
	where	nr_seq_notific_pagador	=	nr_seq_pagador_w;

	delete	from	pls_notificacao_receb
	where	nr_seq_notificacao	=	nr_seq_pagador_w;
	end;
end loop;
close C01;

delete	from pls_notificacao_pagador
where	nr_seq_lote	= nr_seq_lote_p;

update	pls_notificacao_lote
set	vl_saldo		= 0,
	vl_titulo		= 0,
	vl_juros		= 0,
	vl_multa		= 0,
	vl_pagar		= 0,
	vl_rescisao		= 0,
	qt_benef_rescindido	= 0,
	vl_suspensao		= 0,
	qt_benef_suspenso	= 0,
	qt_pagador		= 0,
	qt_beneficiarios	= 0,
	qt_pagador_susp		= 0,
	qt_pagador_resc		= 0,
	dt_geracao		 = NULL
where	nr_sequencia		= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
