CREATE OR REPLACE PROCEDURE pls_desfazer_ppsc_ato_coop (nr_seq_lote_p bigint) AS $body$
BEGIN

delete	from pls_ppsc_ato_movto_item a
where	exists (SELECT	1
		from	pls_ppsc_ato_movimento	x
		where	x.nr_sequencia	= a.nr_seq_movimento
		and	x.nr_seq_lote	= nr_seq_lote_p);

delete	from pls_ppsc_ato_movimento
where	nr_seq_lote	= nr_seq_lote_p;

update	pls_ppsc_lote
set	dt_geracao_lote	 = NULL,
	nm_usuario_lote	 = NULL
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
$body$
