CREATE OR REPLACE PROCEDURE pls_desfazer_program_reajuste ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prog_reajuste_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_prog_reajuste
	where	nr_seq_lote	= nr_seq_lote_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_prog_reajuste_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM pls_prog_reajuste
	where	nr_sequencia	= nr_seq_prog_reajuste_w;

	end;
end loop;
close C01;

update	pls_prog_reajuste_lote
set	dt_geracao_lote	 = NULL,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
