Referencias:
  OPS - Controle de notificacoes de atraso
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
update  pls_notificacao_pagador
set	ie_status		= ie_status_ant,
	ie_status_ant		 = NULL,
	nr_seq_vinculo_pagador	 = NULL,
	dt_recebimento_notif	 = NULL,
	nm_pessoa_notific	 = NULL,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= wheb_usuario_pck.get_nm_usuario,
	ie_tipo_notificacao	 = NULL
where 	nr_sequencia 		= nr_seq_notific_pagador_p
and	(ie_status_ant IS NOT NULL AND ie_status_ant::text <> '');

commit;

end;
$body$
