SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_reprov_proposta ( nr_seq_proposta_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_reprovacao_w			timestamp;
qt_dias_w			numeric(20);
qt_dias_param_w			numeric(20);
ie_status_w			pls_proposta_adesao.ie_status%type;
dt_liberacao_w			pls_proposta_adesao.dt_fim_proposta%type;
nr_seq_beneficiario_w		pls_proposta_beneficiario.nr_sequencia%type;
nr_seq_inclusao_benef_w		pls_inclusao_beneficiario.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_inclusao_benef
	from	pls_proposta_beneficiario
	where	nr_seq_proposta	= nr_seq_proposta_p;


BEGIN

begin
select	coalesce(max(obter_valor_param_usuario(1232, 35, Obter_Perfil_Ativo, nm_usuario_p, 0)), 0)
into STRICT	qt_dias_param_w
;
exception
when others then
	qt_dias_param_w	:= 0;
end;

select	max(dt_reprovacao),
	max(dt_fim_proposta)
into STRICT	dt_reprovacao_w,
	dt_liberacao_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_p;

qt_dias_w	:= clock_timestamp() - dt_reprovacao_w;

if (dt_reprovacao_w IS NOT NULL AND dt_reprovacao_w::text <> '') and (qt_dias_w	> qt_dias_param_w) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 267000, null); /* Ultrapassado o prazo limite para defazer a reprovação dessa proposta.[35] */
end if;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
	ie_status_w	:= 'E';
else
	ie_status_w	:= 'U';
end if;

update	pls_proposta_adesao
set	ie_status		= ie_status_w,
	cd_motivo_reprovacao	= '',
	dt_reprovacao		= '',
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_proposta_p;

open C01;
loop
fetch C01 into
	nr_seq_beneficiario_w,
	nr_seq_inclusao_benef_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_seq_inclusao_benef_w IS NOT NULL AND nr_seq_inclusao_benef_w::text <> '') then
		update	pls_inclusao_beneficiario
		set	ie_status		= 'A'
		where	nr_sequencia		= nr_seq_inclusao_benef_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
