CREATE OR REPLACE PROCEDURE pls_desfazer_rescisao_pag_lote ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  desfazer a rescisão dos pagadores  do lote
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_notific_pagador_w	bigint;
ie_status_ant_w			varchar(2);
nr_seq_contrato_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_status_ant
	from	pls_notificacao_pagador
	where	nr_seq_lote	= nr_seq_lote_p;

C02 CURSOR FOR
	SELECT	nr_seq_contrato
	from	pls_notificacao_item
	where	nr_seq_notific_pagador 	= nr_seq_notific_pagador_w;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_notific_pagador_w,
	ie_status_ant_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_contrato_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		CALL pls_reativar_contrato(	nr_seq_contrato_w,
					'',
					'',
					'',
					clock_timestamp(),
					'',
					'S',
					'S',
					'S',
					'S',
					'S',
					nm_usuario_p,
					cd_estabelecimento_p,
					'N');
		end;
	end loop;
	close C02;

	update	pls_notificacao_pagador
	set	dt_rescisao		 = NULL,
		ie_status		= coalesce(ie_status_ant_w, 'G'),
		nm_usuario_rescisao	= '',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_notific_pagador_w;
	end;
end loop;
close C01;

update	pls_notificacao_lote
set	dt_rescisao	 = NULL,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
