	delete from	pls_regra_preco_ordem_mat
	where	nr_sequencia	= nr_seq_regra_p;
elsif (ie_tipo_regra_p = 2) then
	delete from	pls_regra_preco_ordem_serv
	where	nr_sequencia	= nr_seq_regra_p;
end if;

commit;

end;
