CREATE OR REPLACE PROCEDURE pls_desfazer_solicit_auditoria ( nr_seq_auditoria_p bigint, ds_historico_p text, ie_origem_p text, nm_usuario_p text) AS $body$
DECLARE


/*
 ie_origem_p
  N - Desfazer notificação call center
  J - Desfazer solicitação justificativa
*/
nr_seq_guia_w			bigint;
nr_seq_requisicao_w		bigint;


BEGIN

select	nr_seq_guia,
	nr_seq_requisicao
into STRICT	nr_seq_guia_w,
	nr_seq_requisicao_w
from	pls_auditoria
where	nr_sequencia = nr_seq_auditoria_p;

if (ie_origem_p = 'N') then
	CALL pls_resposta_notif_atendimeto(nr_seq_auditoria_p,null,ds_historico_p,'NE',nm_usuario_p);

elsif (ie_origem_p = 'J') then
	CALL pls_atualizar_justif_auditoria(nr_seq_auditoria_p,nr_seq_requisicao_w,nr_seq_guia_w,ds_historico_p,'R',nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
