SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_subst_item ( nr_seq_analise_item_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_commitar_p text ) AS $body$
DECLARE


nr_seq_conta_w			bigint;
ie_tipo_item_w			varchar(2);
nr_seq_analise_w		bigint;
cd_item_w			bigint;
ds_item_w			varchar(255);
nr_seq_glosa_oc_w		bigint;
nr_seq_item_w			bigint;
ie_substituido_w		varchar(2);
nr_seq_item_criado_w		bigint;
nr_seq_criado_subst_w		bigint;
nr_seq_item_partic_w		bigint;
nr_seq_item_pos_w		bigint;
nr_seq_analise_pos_w		bigint;

nr_seq_item_ww			bigint;
ie_tipo_item_ww			varchar(2);
nr_seq_conta_ww			bigint;
nr_seq_glosa_oc_ww		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_substituido
	from	pls_analise_conta_item
	where	nr_seq_conta = nr_seq_conta_w
	and	((nr_seq_conta_proc = nr_seq_item_w and ie_tipo_item_w = 'P') or (nr_seq_conta_mat = nr_seq_item_w and ie_tipo_item_w = 'M'))
	order by 1;

/*Obter os participantes do procedimento*/

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	w_pls_resumo_conta
	where	nr_seq_item  = nr_seq_item_w
	and	ie_tipo_item = 'R';

C03 CURSOR FOR
	SELECT	nr_sequencia
	from	w_pls_resumo_conta
	where	nr_seq_res_conta_princ = nr_seq_analise_item_p;

C04 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_analise_conta_item
	where	nr_seq_conta = nr_seq_conta_ww
	and	((nr_seq_conta_proc = nr_seq_item_ww and ie_tipo_item_ww = 'P')
	or (nr_seq_conta_mat = nr_seq_item_ww and ie_tipo_item_ww = 'M'))
	order by 1;

C05 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_analise_conta_item
	where	nr_seq_w_resumo_conta = nr_seq_item_criado_w
	order by 1;

BEGIN

select	nr_seq_item,
	ie_tipo_item,
	cd_item,
	CASE WHEN coalesce(ie_origem_proced,0)=0 THEN pls_obter_desc_material(cd_item)  ELSE obter_descricao_procedimento(cd_item,ie_origem_proced) END ,
	nr_seq_analise,
	nr_seq_conta
into STRICT	nr_seq_item_w,
	ie_tipo_item_w,
	cd_item_w,
	ds_item_w,
	nr_seq_analise_w,
	nr_seq_conta_w
from	w_pls_resumo_conta
where	nr_sequencia = nr_seq_analise_item_p;

/*Obter a sequencia do novo item e participantes que foi inserido em substituição*/

open C03;
loop
fetch C03 into
	nr_seq_item_criado_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin

	/*Obter se o próprio item criado foi substituido*/

	select	max(nr_sequencia)
	into STRICT	nr_seq_criado_subst_w
	from	w_pls_resumo_conta
	where	nr_seq_res_conta_princ = nr_seq_item_criado_w;

	/*Se o próprio item criado foi substituido o item*/

	if (coalesce(nr_seq_criado_subst_w,0) > 0) then
		CALL pls_desfazer_subst_item(nr_seq_item_criado_w, nr_seq_grupo_p, nm_usuario_p, cd_estabelecimento_p, 'N');
	end if;

	select	nr_seq_item,
		ie_tipo_item,
		nr_seq_conta
	into STRICT	nr_seq_item_ww,
		ie_tipo_item_ww,
		nr_seq_conta_ww
	from	w_pls_resumo_conta
	where	nr_sequencia = nr_seq_analise_item_p;

	/*Obter as glosas/oc dos itens criados*/

	open C04;
	loop
	fetch C04 into
		nr_seq_glosa_oc_ww;
	EXIT WHEN NOT FOUND; /* apply on C04 */
		begin

		delete	FROM pls_analise_parecer_item
		where	nr_seq_item	= nr_seq_glosa_oc_ww;

		delete	FROM pls_analise_conta_item_log
		where	nr_seq_analise_item	= nr_seq_glosa_oc_ww;

		delete	FROM pls_analise_fluxo_item
		where	nr_seq_glosa_item = nr_seq_glosa_oc_ww;

		delete	FROM pls_analise_conta_item
		where	nr_sequencia	= nr_seq_glosa_oc_ww;

		end;
	end loop;
	close C04;

	open C05;
	loop
	fetch C05 into
		nr_seq_glosa_oc_ww;
	EXIT WHEN NOT FOUND; /* apply on C05 */
		begin

		delete	FROM pls_analise_parecer_item
		where	nr_seq_item	= nr_seq_glosa_oc_ww;

		delete	FROM pls_analise_conta_item_log
		where	nr_seq_analise_item	= nr_seq_glosa_oc_ww;

		delete	FROM pls_analise_fluxo_item
		where	nr_seq_glosa_item = nr_seq_glosa_oc_ww;

		delete	FROM pls_analise_conta_item
		where	nr_sequencia	= nr_seq_glosa_oc_ww;

		end;
	end loop;
	close C05;

	/*Deletar o novo item que foi inserido em substituição*/

	delete  FROM w_pls_resumo_conta
	where	nr_sequencia = nr_seq_item_criado_w;

	end;
end loop;
close C03;

/*Atualizar o status para "Pendente de liberação" e atualizar os valores  liberados para o pré liberação*/

update	w_pls_resumo_conta
set	ie_status		= 'P',
	vl_total		= vl_total_original,
	vl_unitario 		= vl_unitario_original,
	qt_liberado 		= qt_liberado_original,
	vl_total_original	= 0,
	vl_unitario_original 	= 0,
	qt_liberado_original 	= 0
where	nr_sequencia 		= nr_seq_analise_item_p;

/*Apagar os pareceres*/

open C01;
loop
fetch C01 into
	nr_seq_glosa_oc_w,
	ie_substituido_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM pls_analise_parecer_item
	where	nr_seq_item = nr_seq_glosa_oc_w;

	update	pls_analise_conta_item
	set	ie_status 	= 'P',
		ie_situacao 	= 'A'
	where	nr_sequencia = nr_seq_glosa_oc_w
	and	ie_status <> 'I';

	/*Diego Os 307670 - No caso de ser um a glosa gerada na substituição então o sistema deve apagar esta glosa*/

	if (coalesce(ie_substituido_w,'N') = 'S') then

		/* Jean OS 484621 - Excluir os regsitros filhos antes de continuar com a operação*/

		delete  FROM pls_analise_conta_item_log
		where  	nr_seq_analise_item = nr_seq_glosa_oc_w;

		delete	FROM pls_analise_fluxo_item
		where	nr_seq_glosa_item = nr_seq_glosa_oc_w;

		delete	FROM pls_analise_conta_item
		where	nr_sequencia = nr_seq_glosa_oc_w;
	end if;

	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_item_partic_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	update	w_pls_resumo_conta
	set	ie_status		= 'P',
		vl_total		= vl_total_original,
		vl_unitario 		= vl_unitario_original,
		qt_liberado 		= qt_liberado_original,
		vl_total_original	= 0,
		vl_unitario_original 	= 0,
		qt_liberado_original 	= 0
	where	nr_sequencia 		= nr_seq_item_partic_w;

	end;
end loop;
close C02;

/*Inserir o histórico de análise*/

CALL pls_inserir_hist_analise(nr_seq_conta_w, nr_seq_analise_w, 16,
			 nr_seq_item_w, ie_tipo_item_w, null,
			 null, 'Desfeita a substituição do item retornando o item '||cd_item_w||' - '||ds_item_w||' para o status de "Pendente de liberação"',
			 nr_seq_grupo_p, nm_usuario_p, cd_estabelecimento_p);

begin
select	nr_sequencia,
	nr_seq_analise
into STRICT	nr_seq_item_pos_w,
	nr_seq_analise_pos_w
from	w_pls_resumo_conta
where	nr_seq_item_princ = nr_seq_analise_item_p;
exception
when others then
	nr_seq_item_pos_w	:= null;
	nr_seq_analise_pos_w	:= null;
end;

if (coalesce(nr_seq_item_pos_w,0) > 0) then
	/*Diego OS 402760 - Ao desfazer a substituição de um item na produção, se este existir na análise de pós- também deve ser substituido.*/

	CALL pls_desfazer_subst_item(nr_seq_item_pos_w, nr_seq_grupo_p, nm_usuario_p, cd_estabelecimento_p, 'N');
end if;

if (coalesce(ie_commitar_p,'S') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_desfazer_subst_item ( nr_seq_analise_item_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_commitar_p text ) FROM PUBLIC;

