CREATE OR REPLACE PROCEDURE pls_desfazer_susp_atend_lote ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Desfazer a suspensão do atendimento para o lote
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_notific_pagador_w	bigint;
ie_status_ant_w			varchar(10);
nr_seq_pagador_w		pls_notificacao_pagador.nr_seq_pagador%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_status_ant,
		nr_seq_pagador
	from	pls_notificacao_pagador
	where	nr_seq_lote	= nr_seq_lote_p
	and	(dt_suspensao IS NOT NULL AND dt_suspensao::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_notific_pagador_w,
	ie_status_ant_w,
	nr_seq_pagador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL pls_atualizar_susp_atend(null,
				nr_seq_notific_pagador_w,
				'D',
				'',
				'',
				nm_usuario_p,
				'N',
				'N',
				null);

	update	pls_notificacao_pagador
	set	nr_seq_regra_susp	 = NULL,
		dt_suspensao		 = NULL,
		ie_status		= coalesce(ie_status_ant_w, 'G'),
		nm_usuario_suspensao	= nm_usuario_p
	where	nr_sequencia		= nr_seq_notific_pagador_w;

	if (nr_seq_pagador_w IS NOT NULL AND nr_seq_pagador_w::text <> '') then
		update	pls_contrato_pagador
		set	dt_suspensao	 = NULL
		where	nr_sequencia	= nr_seq_pagador_w;
	end if;
	end;
end loop;
close C01;

update	pls_notificacao_lote
set	dt_suspensao	 = NULL,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
