CREATE OR REPLACE PROCEDURE pls_desfazer_titulos_pag_prest ( nr_seq_lote_p bigint, nr_seq_pagamento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w		bigint;						
nr_seq_pag_prestador_w	bigint;					
nr_seq_prestador_w	bigint;				
				 
C01 CURSOR FOR 
	SELECT	c.nr_titulo, 
		b.nr_sequencia, 
		b.nr_seq_prestador 
	from	titulo_pagar c, 
		pls_pagamento_prestador b, 
		pls_lote_pagamento a 
	where	a.nr_sequencia 	= nr_seq_lote_p 
	and	a.nr_sequencia	= b.nr_seq_lote 
	and	c.nr_seq_pls_pag_prest = b.nr_sequencia 
	and	b.nr_sequencia	= nr_seq_pagamento_p;

BEGIN
 
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') and (nr_seq_pagamento_p IS NOT NULL AND nr_seq_pagamento_p::text <> '') then 
	open C01;
	loop 
	fetch C01 into	 
		nr_titulo_w, 
		nr_seq_pag_prestador_w, 
		nr_seq_prestador_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
			CALL cancelar_titulo_pagar(nr_titulo_w, nm_usuario_p, clock_timestamp());
			 
			update 	pls_pag_prest_vencimento 
			set	nr_titulo  = NULL 
			where	nr_seq_pag_prestador 	= nr_seq_pag_prestador_w 
			and	nr_titulo		= nr_titulo_w;
			 
			update	titulo_pagar 
			set	nr_seq_pls_pag_prest  = NULL, 
				ds_observacao_titulo = 	('Este t√≠tulo havia sido gerado pelo lote de pagamento ' || 
							nr_seq_lote_p || ' e o prestador ' || nr_seq_prestador_w || '.')	 
			where	nr_titulo = nr_titulo_w;
			 
			update	titulo_pagar_imposto 
			set	nr_seq_pls_venc_trib  = NULL 
			where	nr_titulo = nr_titulo_w;
		end;
	end loop;
	close C01;
	 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
