SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfazer_vinc_nota_adiant (nr_seq_item_p pls_conta_proc.nr_sequencia%type, ie_tipo_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_w		pls_conta.nr_sequencia%type;
nr_seq_analise_w	pls_analise_conta.nr_sequencia%type;
ds_observacao_w		pls_hist_analise_conta.ds_observacao%type;
nr_seq_nota_fiscal_w	pls_conta_proc.nr_seq_nota_fiscal%type;


BEGIN

if (ie_tipo_p = 'P') then

	select	max(a.nr_seq_conta),
		max(b.nr_seq_analise),
		max(nr_seq_nota_fiscal)
	into STRICT	nr_seq_conta_w,
		nr_seq_analise_w,
		nr_seq_nota_fiscal_w
	from	pls_conta_proc a,
		pls_conta b
	where	a.nr_seq_conta = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_item_p;

	if (nr_seq_nota_fiscal_w IS NOT NULL AND nr_seq_nota_fiscal_w::text <> '') then

		update	pls_conta_proc
		set	nr_seq_nota_fiscal	 = NULL
		where	nr_sequencia		= nr_seq_item_p;

		ds_observacao_w := 'Item desvinculado a nota fiscal de adiantamento com sequência ='||nr_seq_nota_fiscal_w;
		CALL pls_inserir_hist_analise(nr_seq_conta_w, nr_seq_analise_w, 15,
					 nr_seq_item_p, 'P', null,
					 null,ds_observacao_w, null,
					 nm_usuario_p,wheb_usuario_pck.get_cd_estabelecimento);
	end if;

end if;

if (ie_tipo_p = 'M') then

	select	max(a.nr_seq_conta),
		max(b.nr_seq_analise),
		max(nr_seq_nota_fiscal)
	into STRICT	nr_seq_conta_w,
		nr_seq_analise_w,
		nr_seq_nota_fiscal_w
	from	pls_conta_mat a,
		pls_conta b
	where	a.nr_seq_conta = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_item_p;

	if (nr_seq_nota_fiscal_w IS NOT NULL AND nr_seq_nota_fiscal_w::text <> '') then

		update	pls_conta_mat
		set	nr_seq_nota_fiscal	 = NULL
		where	nr_sequencia		= nr_seq_item_p;

		ds_observacao_w := 'Item desvinculado a nota fiscal de adiantamento com sequência ='||nr_seq_nota_fiscal_w;
		CALL pls_inserir_hist_analise(nr_seq_conta_w, nr_seq_analise_w, 15,
					 nr_seq_item_p, 'M', null,
					 null,ds_observacao_w, null,
					 nm_usuario_p,wheb_usuario_pck.get_cd_estabelecimento);
	end if;


end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
