CREATE OR REPLACE PROCEDURE pls_desfaz_event_movto_repas ( nr_repasse_terceiro_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

qt_item_w	integer;

C01 CURSOR FOR
	SELECT	b.nr_seq_lote,
		b.nr_sequencia nr_seq_movimento
	from	pls_evento_movimento	b,
		pls_lote_evento		a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	b.nr_repasse_terceiro	= nr_repasse_terceiro_p
	and not exists (SELECT	1
			from	pls_pagamento_item	e
			where	e.nr_sequencia		= b.nr_seq_pagamento_item)
	order by b.nr_sequencia;

BEGIN

for r_C01_w in C01 loop

	delete 	FROM pls_evento_movimento
	where 	nr_sequencia 	= r_C01_w.nr_seq_movimento;

	select	count(1)
	into STRICT	qt_item_w
	from	pls_evento_movimento
	where	nr_seq_lote	= r_C01_w.nr_seq_lote;

	if (coalesce(qt_item_w,0) = 0) then
		delete 	FROM pls_lote_evento
		where	nr_sequencia	= r_C01_w.nr_seq_lote;
	end if;

end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
