SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desfaz_solic_proces_intern ( nr_seq_analise_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_processo_interno_w		varchar(255);
nr_seq_requisicao_w		bigint;
nr_seq_guia_w			bigint;
nr_seq_execucao_w		bigint;
nr_seq_proc_interno_w		bigint;


BEGIN

begin
	select	nr_seq_proc_interno
	into STRICT	nr_seq_proc_interno_w
	from	pls_auditoria
	where	nr_sequencia	= nr_seq_analise_p;
exception
when others then
	nr_seq_proc_interno_w	:= 0;
end;

begin
	select	substr(ds_processo_interno,1,255)
	into STRICT	ds_processo_interno_w
	from	pls_analise_proc_interno
	where	nr_sequencia	= nr_seq_proc_interno_w;
exception
when others then
	ds_processo_interno_w	:= 'X';
end;

update	pls_auditoria
set	ie_status		= 'A',
	nr_seq_proc_interno	 = NULL,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_analise_p;

begin
	select	nr_seq_requisicao,
		nr_seq_guia,
		nr_seq_execucao
	into STRICT	nr_seq_requisicao_w,
		nr_seq_guia_w,
		nr_seq_execucao_w
	from	pls_auditoria
	where	nr_sequencia	= nr_seq_analise_p;
exception
when others then
	nr_seq_requisicao_w	:= 0;
	nr_seq_guia_w		:= 0;
	nr_seq_execucao_w	:= 0;
end;

if (coalesce(ds_processo_interno_w,'X')	<> 'X') then
	if (coalesce(nr_seq_requisicao_w,0)	<> 0) then
		CALL pls_requisicao_gravar_hist(	nr_seq_requisicao_w, 'L', 'O usuário '||nm_usuario_p||' desfez a solicitação de processo interno '||ds_processo_interno_w||
						' em '||to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss'), null, nm_usuario_p);
	elsif (coalesce(nr_seq_guia_w,0)	<> 0) then
		CALL pls_guia_gravar_historico(	nr_seq_guia_w, 2, 'O usuário '||nm_usuario_p||' desfez a solicitação de processo interno '||ds_processo_interno_w||
						' em '||to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss'), '', nm_usuario_p);
	elsif (coalesce(nr_seq_execucao_w,0)	<> 0) then
		select	max(nr_seq_requisicao)
		into STRICT	nr_seq_requisicao_w
		from	pls_execucao_requisicao
		where	nr_sequencia	= nr_seq_execucao_w;

		if (coalesce(nr_seq_requisicao_w,0)	<> 0) then
			CALL pls_requisicao_gravar_hist(	nr_seq_requisicao_w, 'L', 'O usuário '||nm_usuario_p||' desfez a solicitação de processo interno '||ds_processo_interno_w||
							' em '||to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss'), nr_seq_execucao_w, nm_usuario_p);
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
