CREATE OR REPLACE PROCEDURE pls_desfa_iten_even_pos_tribut (nr_seq_lote_pagamento_p pls_lote_pagamento.nr_sequencia%type) AS $body$
DECLARE


c01 CURSOR(	nr_seq_lote_pagamento_pc	pls_lote_pagamento.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia,
		a.nr_seq_lote,
		b.vl_item
	from	pls_evento_regra_fixo	c,
		pls_pagamento_item	b,
		pls_pagamento_prestador	a
	where	a.nr_seq_lote		= nr_seq_lote_pagamento_pc
	and	a.nr_sequencia		= b.nr_seq_pagamento
	and	c.nr_sequencia		= b.nr_seq_regra_fixo_calc
	and	coalesce(c.ie_gerar_apos_tributacao,'N') = 'S';

BEGIN

for r_C01_w in C01( nr_seq_lote_pagamento_p ) loop
	delete	from pls_pagamento_item a
	where nr_sequencia = r_C01_w.nr_sequencia;

	update	pls_lote_pagamento
	set	vl_lote = vl_lote + abs(r_c01_w.vl_item)
	where	nr_sequencia = r_c01_w.nr_seq_lote;
end loop;

delete	from pls_evento_movimento a
where	exists (SELECT	1
		from	pls_lote_evento x
		where	x.nr_sequencia = a.nr_seq_lote
		and	x.nr_seq_pgto_desc_liq = nr_seq_lote_pagamento_p);

delete	from pls_lote_evento
where	nr_seq_pgto_desc_liq = nr_seq_lote_pagamento_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
