SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desf_acatar_negar_rec_glo ( nr_seq_ref_p bigint, ie_tipo_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Desfazer valor acatado / negado dos itens de recurso de glosa
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P
'M' - Material
'P' - Procedimento
IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P

Alterações:
-------------------------------------------------------------------------------------------------------------------
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_glosa_w		integer;
nr_seq_conta_rec_w	pls_rec_glosa_conta.nr_sequencia%type;


BEGIN

if (ie_tipo_p = 'P') then
	update	pls_rec_glosa_proc
	set	ds_justificativa_oper 	= '',
		dt_atualizacao		= clock_timestamp(),
		ie_status		= '1',
		vl_acatado		= 0
	where	nr_sequencia		= nr_seq_ref_p;

	select	max(nr_seq_conta_rec)
	into STRICT	nr_seq_conta_rec_w
	from	pls_rec_glosa_proc
	where	nr_sequencia = nr_seq_ref_p;

	CALL pls_gerar_log_rec_glosa( 'DANP', null, nr_seq_ref_p, nm_usuario_p, 'P', null, null);
elsif (ie_tipo_p = 'M') then
	update	pls_rec_glosa_mat
	set	ds_justificativa_oper 	= '',
		dt_atualizacao		= clock_timestamp(),
		ie_status		= '1',
		vl_acatado		= 0
	where	nr_sequencia		= nr_seq_ref_p;

	select	max(nr_seq_conta_rec)
	into STRICT	nr_seq_conta_rec_w
	from	pls_rec_glosa_mat
	where	nr_sequencia = nr_seq_ref_p;

	CALL pls_gerar_log_rec_glosa( 'DANM', null, nr_seq_ref_p, nm_usuario_p, 'M', null, null);
end if;

select	count(1)
into STRICT	qt_glosa_w
from	pls_rec_glosa_glosas
where	nr_seq_conta_rec = nr_seq_conta_rec_w
and	coalesce(nr_seq_proc_rec::text, '') = ''
and	coalesce(nr_seq_mat_rec::text, '') = '';

if (qt_glosa_w = 0) then
	update	pls_rec_glosa_conta
	set	ds_justificativa_oper  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_conta_rec_w;
end if;

if (nr_seq_conta_rec_w IS NOT NULL AND nr_seq_conta_rec_w::text <> '') then
	CALL pls_atualizar_valor_recurso(nr_seq_conta_rec_w,'C',nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
