SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desf_confirm_entrega_car ( nr_seq_carteira_emissao_p pls_carteira_emissao.nr_sequencia%type) AS $body$
DECLARE


nr_seq_estagio_w		pls_segurado_cart_estagio.nr_sequencia%type;
nr_seq_devolucao_w		pls_segurado_cart_estagio.nr_seq_devolucao%type;
nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
nr_via_emissao_w		pls_carteira_emissao.nr_via_emissao%type;
nr_seq_carteira_w		pls_segurado_carteira.nr_sequencia%type;

BEGIN

select	max(a.nr_seq_segurado),
	max(b.nr_via_emissao),
	max(a.nr_sequencia)
into STRICT	nr_seq_segurado_w,
	nr_via_emissao_w,
	nr_seq_carteira_w
from	pls_segurado_carteira 	a,
	pls_carteira_emissao 	b
where	a.nr_sequencia	= b.nr_seq_seg_carteira
and	b.nr_sequencia	= nr_seq_carteira_emissao_p;

if (nr_via_emissao_w IS NOT NULL AND nr_via_emissao_w::text <> '') then
	select	max(nr_seq_devolucao)
	into STRICT	nr_seq_devolucao_w
	from	pls_segurado_cart_estagio
	where	nr_seq_cartao_seg	= nr_seq_carteira_w
	and	nr_via			= nr_via_emissao_w
	and	ie_estagio_emissao	= '8';

	if (nr_seq_devolucao_w > 0) then
		--A confirmação não pode ser defeita pois houve devolução da carteira do beneficiário #@DS_BENEFICIARIO@#.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1041094, 'DS_BENEFICIARIO=' || substr(pls_obter_nome_beneficiario(nr_seq_segurado_w),1,255));
	end if;
end if;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_estagio_w
from	pls_segurado_cart_estagio
where	nr_seq_cart_emissao	= nr_seq_carteira_emissao_p
and	ie_estagio_emissao	= '7';

if (nr_seq_estagio_w > 0) then
	delete	FROM pls_segurado_cart_estagio
	where	nr_sequencia	= nr_seq_estagio_w;

	update	pls_carteira_emissao
	set	dt_entrega_beneficiario	 = NULL,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= wheb_usuario_pck.get_nm_usuario,
		nm_usuario_entrega	 = NULL,
		nm_pessoa_entrega	 = NULL
	where	nr_sequencia		= nr_seq_carteira_emissao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_desf_confirm_entrega_car ( nr_seq_carteira_emissao_p pls_carteira_emissao.nr_sequencia%type) FROM PUBLIC;

