	CALL pls_desf_lib_item_analise(	nr_seq_item_w_resumo_w, nr_seq_analise_p, cd_estabelecimento_p, 
					nr_seq_grupo_atual_p, nm_usuario_p, 'N'	);
	 
	end;
end loop;
close C01;
 
