ie_tipo_despesa_w		varchar(5);
qt_apresentado_w		varchar(9);

 
C01 CURSOR FOR 
	SELECT	qt_ocorrencia_pend, 
		qt_apresentado, 
		ie_tipo_despesa 
	from	pls_resumo_conta 
	where	nr_seq_conta	= nr_seq_conta_p;


