EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		if (qt_ocorrencia_pend_w = 0 AND qt_apresentado_w <> 0) then 
			CALL pls_desfazer_lib_item_despesa(nr_seq_conta_p, ie_tipo_despesa_w, cd_estabelecimento_p, nm_usuario_p);
		end if;
	end;
end loop;
close C01;
 
end;
