SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desf_subst_item_conta ( ie_tipo_p text, nr_seq_item_p bigint, nr_seq_conta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_conta_proc_w		bigint;
nr_seq_conta_mat_w		bigint;
nr_seq_proc_participante_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_proc_participante
	where	nr_seq_conta_proc = nr_seq_item_p
	and	coalesce(ie_status,'U') <> 'C'
	order by 1;
C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta_proc
	where	nr_seq_proc_princ = nr_seq_item_p
	order by 1;
C03 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta_mat
	where	nr_seq_mat_princ = nr_seq_item_p
	order by 1;

BEGIN

if (ie_tipo_p	= 'P') then
	open C02;
	loop
	fetch C02 into
		nr_seq_conta_proc_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		open C01;
		loop
		fetch C01 into
			nr_seq_proc_participante_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			delete	FROM pls_ocorrencia_benef
			where	nr_seq_proc_partic	= nr_seq_proc_participante_w;

			delete	FROM pls_conta_glosa
			where	nr_seq_proc_partic	= nr_seq_proc_participante_w;

			delete	FROM pls_proc_participante
			where	nr_sequencia	= nr_seq_proc_participante_w;

			end;
		end loop;
		close C01;
		insert into	pls_conta_log(	nr_sequencia, nm_usuario, dt_atualizacao,
							nm_usuario_nrec, dt_atualizacao_nrec, nm_usuario_alteracao,
							dt_alteracao, nr_seq_conta, nr_seq_conta_proc,
							ds_alteracao)
				values (	nextval('pls_conta_log_seq'), nm_usuario_p, clock_timestamp(),
							nm_usuario_p, clock_timestamp(), nm_usuario_p,
							clock_timestamp(), nr_seq_conta_p, nr_seq_item_p,
							'Conta proc: '||nr_seq_item_p||' desfeita a substituição pela conta '||nr_seq_conta_proc_w);
		update	pls_conta_proc
		set	ie_status 	= 'U',
			ie_glosa	= 'N',
			vl_liberado	= 0,
			vl_unitario	= 0,
			qt_procedimento	= 0
		where	nr_sequencia	= nr_seq_item_p;

		update	pls_proc_participante
		set	ie_status	= 'U'
		where	nr_seq_conta_proc = nr_seq_item_p;

		delete	FROM pls_ocorrencia_benef
		where	nr_seq_proc	= nr_seq_conta_proc_w;

		delete	FROM pls_conta_glosa
		where	nr_seq_conta_proc	= nr_seq_conta_proc_w;

		delete	FROM pls_conta_proc
		where	nr_sequencia = nr_seq_conta_proc_w;

		end;
	end loop;
	close C02;

else
	open C03;
	loop
	fetch C03 into
		nr_seq_conta_mat_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin
		update	pls_conta_mat
		set	ie_status 	= 'U',
			ie_glosa	= 'N',
			vl_liberado	= 0,
			vl_unitario	= 0,
			qt_material	= 0
		where	nr_sequencia	= nr_seq_item_p;

		insert into	pls_conta_log(	nr_sequencia, nm_usuario, dt_atualizacao,
							nm_usuario_nrec, dt_atualizacao_nrec, nm_usuario_alteracao,
							dt_alteracao, nr_seq_conta, nr_seq_conta_mat,
							ds_alteracao)
				values (	nextval('pls_conta_log_seq'), nm_usuario_p, clock_timestamp(),
							nm_usuario_p, clock_timestamp(), nm_usuario_p,
							clock_timestamp(), nr_seq_conta_p, nr_seq_item_p,
							'Conta mat: '||nr_seq_item_p||' desfeita a substituição do item pela conta '||nr_seq_conta_mat_w);

		delete	FROM pls_conta_glosa
		where	nr_seq_conta_mat	= nr_seq_conta_mat_w;

		delete	FROM pls_ocorrencia_benef
		where	nr_seq_mat	= nr_seq_conta_mat_w;

		delete	FROM pls_conta_mat
		where	nr_sequencia = nr_seq_conta_mat_w;

		end;
	end loop;
	close C03;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_desf_subst_item_conta ( ie_tipo_p text, nr_seq_item_p bigint, nr_seq_conta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

